mapper.select:
SELECT b.*
FROM (
SELECT @ROWNUM := @ROWNUM + 1 AS rn, p.*
FROM PRODUCT p
JOIN category c ON p.category_num = c.category_num
CROSS JOIN (SELECT @ROWNUM := 0) r
WHERE c.category_num = #{categoryNum}
) b
WHERE b.rn BETWEEN #{first} AND #{last}

mapper.update:
update product 
SET  category_num = #{categoryNum},
	pro_name = #{proName}
	,pro_img1 = #{proImg1}
	,pro_img2 = #{proImg2}
	,pro_img3 = #{proImg3},
	pro_content = #{proContent},
	pro_price = #{proPrice},
	pro_date = sysdate(),
	pro_cnt = #{proCnt}
where product_num =#{productNum}

mapper.select:
SELECT pro_cnt
FROM product
where product_num = #{productNum}
